import React,{useState} from "react";
import './AddInventory.css';
import axios from "axios";
import jsPDF from 'jspdf';
import logo from '../assets/logo.PNG';

export default function AddInventory(){

  const pdfGenerate=pdfGenerate=()=>{
    var doc=new jsPDF('landscape','px','a4','false');
    doc.addImage(logo,'PNG',65,20,500,400)
    doc.save('P.pdf')
  }
  

  const [qinstock, setQinstock] =useState("");
  const [name, setName] =useState("");
  const [category, setCategory] =useState("");
  const [qusedpresent, setQusedpresent] =useState("");
  const [unitprice, setUnitprice] =useState("");
  const [totcost, setTotcost] =useState("");

 

  function sendData(e){
    e.preventDefault();


  
  
  const newInventory ={
    qinstock, 
        name,
        category,
        qusedpresent,
        unitprice,
        totcost
  }
  axios.post("http://localhost:8050/inventory/adding", newInventory).then(()=>{
    alert("Inventory item was added successfully!")


  }).catch((err)=>{
    alert(err);
  })


 
}

 
  

    return (
      <div className="container">
        <div className="col d-flex justify-content-center">
  <div className="card1">
  <img className="card1-img-top"/>
      <div className="col-md-8 mt-4 mx-auto">
      <h1 className="h3 mb-3 font-weight-normal">Adding New Inventory Item</h1> 
      <form className="needs-validation"  onSubmit={sendData}>
       
                            <div className="form-group" style={{marginBottom:'15px'}} align="left">  <div className="col-xs-4">
                  
<label style={{marginBottom:'5px'}}>Quantity In Stock</label></div>
<input type="text" className="form-control" name="qinstock" placeholder="Enter quantity in stock" onChange={(e)=>{
setQinstock(e.target.value);

}}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Item Name</label>
                            <input type="text" 
                            className="form-control"
                            name="name"
                            placeholder="Enter inventory item name" onChange={(e)=>{
                              setName(e.target.value);
                              
                              }}/>
          </div>
        
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Item Category</label>
                            <input type="text" 
                            className="form-control"
                            name="category"
                            placeholder="Enter category" onChange={(e)=>{
                              setCategory(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Quantity Of the use Present</label>
                            <input type="text" 
                            className="form-control"
                            name="qusedpresent"
                            placeholder="Enter present use item quantity" onChange={(e)=>{
                              setQusedpresent(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Unit Price</label>
                            <input type="text" 
                            className="form-control"
                            name="unitprice"
                            placeholder="Enter unit price of item" onChange={(e)=>{
                              setUnitprice(e.target.value);
                              
                              }}/>
          </div>
        
          
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Total Cost</label>
                            <input type="text" 
                            className="form-control"
                            name="totcost"
                            placeholder="Enter total cost of inventory in the stock" onChange={(e)=>{
                              setTotcost(e.target.value);
                              
                              }}/>
          </div>
          
         
          <div className="form-group" style={{marginBottom:'15px'}} >
          <table>
            <tr><th>
            <button type="submit" className="btn btn-success">Add Inventory</button></th>
            <th></th>
            <th>
            <a href="/home" type="button" class="btn btn-secondary">Home</a></th>
            <th></th>
            <th>
            <button onClick={this.pdfGenerate}>Generate Report</button></th>
            </tr></table></div>
        

  

      
              
     
      
      </form>
  </div>
  </div>
  </div>
  </div>
    );
}
