import React, { useState, useEffect } from 'react';
import axios from "axios";
import {useHistory, useParams} from 'react-router-dom';
import "./AddMaterial.css";


function UpdateMaterial() {


 let history = useHistory();
 const {id} = useParams();
 const [users,setUsers]= useState({
    matname:"", 
    matcategory:"",
    matunit:"",
    maxlevel:"",
    amtissued:"",
    matunitprice:"",
    mtotcost:""
 

 });
   
 const{matname, matcategory,matunit,maxlevel, amtissued,matunitprice,mtotcost} = users;
 const onInputChange = e =>{
   setUsers({...users, [e.target.name]:e.target.value});
 };
   
    
      useEffect(() =>{
    loadUser();
 }, []);    

   const onSubmit = async e =>{
   e.preventDefault();

   await axios.put(`http://localhost:8050/material/updated/${id}`,users)
     history.push('/');
     alert("Material item is updated successfully!")

     };


     const loadUser = async () =>{

       const result = await axios.get(`http://localhost:8050/material/gets/${id}`);
       setUsers(result.data);
       
     };

/*
  

  function EditInventory(props) {
  let id = props.match.params.id;

  const [data, setData] = useState("");

  useEffect(() => {
    axios.get(`http://localhost:8050/inventory/${id}`).then((res) => {
      setData(res.data);
      console.log(res.data);
    }).catch((err)=>{
      console.log(err);
    })
  },[])

  const onInputChange = e => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };



  function onSubmit() {

    axios.put(`http://localhost:8050/inventory/update/${id}`, data).then(()=>{
      alert("Updated successfully"); 
    }).catch((err) => {
      console.log(err);
    })
    window.location.replace('/');
  };
*/
return (
  <div className="container">
  <div className="col d-flex justify-content-center">
<div className="card1">
<img className="card1-img-top"/>
    <div className="col-md-8 mt-4 mx-auto">
      <h1 className="h3 mb-3 font-weight-normal">Update Raw Material Item</h1> 
      <form className="needs-validation"  onSubmit={e => onSubmit(e)}>

      <div className="form-group" style={{marginBottom:'15px'}} align="left">
              <label style={{marginBottom:'5px'}}>Materail ID</label><br/>
              <input type="text" className="form-control"   name="id" value={id} onChange={(e)=>onInputChange(e)} readOnly required/>
              </div>

              <div className="form-group1" style={{marginBottom:'15px'}} align="left">
                <label for="matname" className="form-label" style={{marginBottom:'5px'}}>Raw Material Name</label><br/>
                <input type="text" className="form-control" id="matname" placeholder="Enter material name" name="matname" value={matname}  onChange={(e)=>onInputChange(e)} required  />
                </div>
                <div className="form-group2" style={{marginBottom:'15px'}} align="left">
                  <label for="matcategory" className="form-label" style={{marginBottom:'5px'}}>Raw Material Category</label><br/>
                  <input type="text" className="form-control"  id="matcategory" placeholder="Enter material category" name="matcategory" value={matcategory} onChange={(e)=>onInputChange(e)} required />
                  </div>
                  <div className="form-group3" style={{marginBottom:'15px'}} align="left">
                    <label for="matunit" className="form-label" style={{marginBottom:'5px'}}>Material Unit</label><br/>
                    <input type="text" className="form-control" id="matunit"  placeholder="Enter material unit"  name="matunit" value={matunit} onChange={(e)=>onInputChange(e)} required />
                    </div>
                    <div className="form-group4" style={{marginBottom:'15px'}} align="left">
                      <label for="maxlevel" className="form-label" style={{marginBottom:'5px'}}>Maximum Stock Level</label><br/>
                            <input type="text" className="form-control" id="maxlevel" name="maxlevel" value={maxlevel} placeholder="Enter maximum stock level" onChange={(e)=>onInputChange(e)} required />
                            </div>
                            <div className="form-group5" style={{marginBottom:'15px'}} align="left">
                              <label for="amtissued" className="form-label" style={{marginBottom:'5px'}}>Amount Issued Per Day</label><br/>
                              <input type="text" className="form-control" id="amtissued" name="amtissued" placeholder="Enter ammount issued per day" value={amtissued} onChange={(e)=>onInputChange(e)} required />
                              </div>
                              <div className="form-group6" style={{marginBottom:'15px'}} align="left">
                                <label for="matunitprice" className="form-label" style={{marginBottom:'5px'}}>Unit Price</label><br/>
                                <input type="text" className="form-control" id="matunitprice" name="matunitprice" placeholder="Enter unit price" value={matunitprice} onChange={(e)=>onInputChange(e)} required />
                                </div>
                                <div className="form-group7" style={{marginBottom:'15px'}} align="left">
                                <label for="mtotcost" className="form-label" style={{marginBottom:'5px'}}>Total Cost</label><br/>
                                <input type="text" className="form-control" id="mtotcost" name="mtotcost" placeholder="Enter total cost" value={mtotcost} onChange={(e)=>onInputChange(e)} required />
                                </div>
                                <div className="form-group8" style={{marginBottom:'15px'}} >
                                <table>
                                  <tr>
                                  <th>
                                  <button type="submit" className="btn btn-success">Update and Save</button></th>
                                  <th></th>
                                  <th>
                                    <a href="/home" type="button" class="btn btn-secondary">Cancel Process</a></th>
                                    </tr></table>
                                    </div>
                                    </form>
                                    </div>
                                    </div>
                                    </div>
                                    </div>
                                    );
                                  }
export default UpdateMaterial;
