import React,{useState} from "react";

import axios from "axios";
export default function AddMaterial(){

  const [matname, setMatname] =useState("");
  const [matcategory, setMatcategory] =useState("");
  const [matunit, setMatunit] =useState("");
  const [maxlevel, setMaxlevel] =useState("");
  const [amtissued, setAmtissued] =useState("");
  const [matunitprice, setMatunitprice] =useState("");
  const [mtotcost, setMtotcost] =useState("");
 

  function sendData(e){
    e.preventDefault();

  
  
  const newMaterial={
    matname, 
    matcategory,
    matunit,
    maxlevel,
    amtissued,
    matunitprice,
    mtotcost
  }
  axios.post("http://localhost:8050/material/added", newMaterial).then(()=>{
    alert("Material is added!")
  }).catch((err)=>{
    alert(err);
  })
 
}
 
  

    return (
      <div className="col-md-8 mt-4 mx-auto">
      <h1 className="h3 mb-3 font-weight-normal">Adding New Raw Material</h1> 
      <form className="needs-validation"  onSubmit={sendData}>
       
                            <div className="form-group" style={{marginBottom:'15px'}} align="left">
                  
<label style={{marginBottom:'5px'}}>Raw Material Name</label><br/>
<input type="text" className="form-control" name="matname" placeholder="Enter material name" onChange={(e)=>{
setMatname(e.target.value);

}}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Raw Material Category</label>
                            <input type="text" 
                            className="form-control"
                            name="matcategory"
                            placeholder="Enter material category" onChange={(e)=>{
                              setMatcategory(e.target.value);
                              
                              }}/>
          </div>
        
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Material Unit (Ex: Kg, g, mg)</label>
                            <input type="text" 
                            className="form-control"
                            name="matunit"
                            placeholder="Enter material unit" onChange={(e)=>{
                              setMatunit(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Maximum Stock Level</label>
                            <input type="text" 
                            className="form-control"
                            name="maxlevel"
                            placeholder="Enter maximum stock level" onChange={(e)=>{
                              setMaxlevel(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Amount Issued Per Day</label>
                            <input type="text" 
                            className="form-control"
                            name="amtissued"
                            placeholder="Enter amount issued per day" onChange={(e)=>{
                              setAmtissued(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Unit Price</label>
                            <input type="text" 
                            className="form-control"
                            name="matunitprice"
                            placeholder="Enter unit price of material" onChange={(e)=>{
                              setMatunitprice(e.target.value);
                              
                              }}/>
          </div>
          
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Total Cost</label>
                            <input type="text" 
                            className="form-control"
                            name="mtotcost"
                            placeholder="Enter total cost of material in the stock" onChange={(e)=>{
                              setMtotcost(e.target.value);
                              
                              }}/>
          </div>
          
         
          <div className="form-group" style={{marginBottom:'15px'}} >

          <button className="btn btn-success" type="submit">
              <i className="far fa-check-square"></i>
              &nbsp; Save
          </button>
          
          <button className="btn btn-primary ml-3" type="submit">
           
           <i className="far fa-window-close"></i>
               &nbsp; Cancel
           </button></div>

  

      
              
     
      
      </form>
  </div>
    );
}
