import React, { useState,useEffect } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";

const AllInventories=()=>{
    const [inventories, setInventories] = useState([]);

    useEffect(()=>{
        loadUsers();
    },[]);

    const loadUsers = async ()=>{
        const result = await axios.get("http://localhost:8050/inventory/");
        setInventories(result.data.reverse());
    };

    function deleteInventory(id){
        axios.delete(`http://localhost:8050/inventory/delete/${id}`).then(()=>{
        alert("Delete Successful");

        }).catch((err)=>{
            console.log(err);
        })
        window.location.reload();
    }
  
    

    

    return (
        
            
    <div className="container">

<div className="row">
  <div className="col-lg-9 mt-2 mb-2" align="left">
    <h4>All Inventory Details</h4>
</div>

        </div>

<div class="epay">




<div className="conatiner">
  
  <table class="table table-hover border-primary">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
  
  </tbody>
</table>

</div>
 </div>
</div>





            
   
    )

}

export default AllInventories;
