import React, { useState,useEffect } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";

const AllInventories=()=>{
    const [inventories, setInventories] = useState([]);

    useEffect(()=>{
        loadUsers();
    },[]);

    const loadUsers = async ()=>{
        const result = await axios.get("http://localhost:8050/inventory/");
        setInventories(result.data.reverse());
    };

    function deleteInventory(id){
        axios.delete(`http://localhost:8050/inventory/delete/${id}`).then(()=>{
        alert("Delete Successful");

        }).catch((err)=>{
            console.log(err);
        })
        window.location.reload();
    }
  
    

    

    return (
        
            
    <div className="container">

<div className="fun">

<span>Payment Management</span>

</div>

<div class="epay">
<Link to={'/adding'} >
        <button type="submit" className="addvbtn"><b>+Add New Payment</b></button>
      </Link>


<div class="search-container">
  <form className="search">
    <input className="search123" type="text" placeholder="Search Payment" name="search1" />
    <Link to={'/get/:id'} >
      <button className="btn13" type="submit">Search</button>
    </Link>
  </form>
</div>


<div className="conatiner">
  <center><h3>ALL INVENTORY DETAILS</h3></center>
  <table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td colspan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>


 
</div>
</div>
</div>




            
   
    )

}

export default AllInventories;
