          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}} className="textfield2">Postal Code</label>
                            <input type="text" 
                            className="form-control"
                            name="postalcode"
                            value={postalcode}
                            placeholder="Enter postal code" 
                            onChange={(e)=>onInputChange(e)} required/>
                             
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}} className="textfield2">Country</label>
                            <input type="text" 
                            className="form-control"
                            name="country"
                            value={country}
                            placeholder="Enter country" onChange={(e)=>onInputChange(e)} required/>
                              
                            
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}} className="textfield2">Company Telephone Number</label>
                            <input type="text" 
                            className="form-control"
                            name="telephone"
                            value={telephone}
                            pattern="[0-9]{10}"
                            placeholder="Enter telephone number" onChange={(e)=>onInputChange(e)} required/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}} className="textfield2">Fax</label>
                            <input type="text" 
                            className="form-control"
                            name="fax"
                            value={fax}
                            pattern="[0-9]{10}"
                            placeholder="Enter fax number" onChange={(e)=>onInputChange(e)} required/>
                              
                         
          </div>
          
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}} className="textfield2">Email Address</label>
                            <input type="email" 
                            className="form-control"
                            name="email"
                            value={email}
                            placeholder="ABC@gmail.com" onChange={(e)=>onInputChange(e)}  required/>
                              
                        
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
