import React, { Component } from 'react';
import PDF from './PDF';
import './Post.css';
import './AllInventory.css'
import AllInventories from './AllInventoryPrint';
class Post extends Component {
    state = {
        inid: '',
        qinstock: '',
        name: '',
        category: '',
        qusedpresent:'',
        unitprice:'',
        totcost:'',
        postSubmitted: false
    }



    render(){
        return(
            <>
                {  !this.state.postSubmitted ? 
                    (<div className="container">
                       
                            <AllInventories/>
                            <div style={{marginBottom:'15px'}} >
         
         <button type="submit" className="btn btn-success">Preview Created Report</button></div>
                    </div>) : (
                        <PDF  />
                        
                    )
                }
            </>
        );
    }
}

export default Post;
