return (
    <div className="container">
  <div className="col d-flex justify-content-center">
<div className="card4">
<img className="card4-img-top"/>
    <div className="col-md-8 mt-4 mx-auto">
      <h3 className="h3 mb-3 font-weight-normal">Update Existing Supplier</h3> 
      <form className="needs-validation"  onSubmit={e => onSubmit(e)}>

      <div className="form-group" style={{marginBottom:'15px'}} align="left">
              <label style={{marginBottom:'5px'}}>Supplier ID</label><br/>
              <input type="text" className="form-control"   name="id" value={id} onChange={(e)=>onInputChange(e)} readOnly required/>
              </div>

              <div className="form-group" style={{marginBottom:'15px'}} align="left"><div className="col-xs-4">
          <label style={{marginBottom:'5px'}}>Applying for this by</label></div>
          <input type="radio" id="applyby" name="applyby" value={applyby} onChange={(e)=>onInputChange(e)} readOnly required/></div>
                            <div className="form-group" style={{marginBottom:'15px'}} align="left">
                  
<label style={{marginBottom:'5px'}}>Company Name</label><br/>
