import React, { useState,useEffect } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";
import "./AllInventory.css";
import Itemcard from "./Itemcard"; 
import data from "./data";
import "./chart.css"
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const data1 = [
  {
    name: 'Wire Cooling Racks',
    "Used Present": 30,
    "Quantity In Stock": 10,
    
  },
  {
    name: 'Page B',
    uv: 3000,
    "Quantity In Stock": 1398,
    amt: 2210,
  },
  {
    name: 'Page C',
    uv: 2000,
    "Quantity In Stock": 2800,
    amt: 2290,
  },
  {
    name: 'Page D',
    uv: 2780,
    "Quantity In Stock": 508,
    amt: 2000,
  },
  {
    name: 'Page E',
    uv: 1890,
    "Quantity In Stock": 500,
    amt: 2181,
  },
  {
    name: 'Page F',
    uv: 2390,
    "Quantity In Stock": 300,
    amt: 2500,
  },
  {
    name: 'Page G',
    uv: 3490,
    "Quantity In Stock": 2500,
    amt: 2100,
  },
];




const AllInventories=()=>{
    const [inventories, setInventories] = useState([]);

    useEffect(()=>{
        loadUsers();
    },[]);

    const loadUsers = async ()=>{
        const result = await axios.get("http://localhost:8050/inventory/");
        setInventories(result.data.reverse());
    };

    function deleteInventory(id){
        axios.delete(`http://localhost:8050/inventory/delete/${id}`).then(()=>{
        /*alert("Delete Successful");*/

        }).catch((err)=>{
            console.log(err);
        })
        window.location.reload();
        alert("Do you want to delete selected item?");
    }
    
  

    

    return (
        
            
    <div className="container">
       <div className="container1">
 <h2 className="text-center mt-3">Compared to Last month</h2>
 <section className="py-4 container">
     <div className="row justify-content-center">
         {data.ItemData.map((item,index)=>{
             return (
                 <Itemcard title={item.title} desc1={item.desc1} desc2={item.desc2} desc3={item.desc3} desc4={item.desc4} button={item.button} key={index}/> 
               
              
               
               

             )
         })}  
      
     </div>
 </section>
 </div>

<div className="row">
  <div className="col-lg-9 mt-2 mb-2">
    
</div>


        </div>






<div className="conatiner">

<div className="col d-flex justify-content-center">
  <div className="card0" style={{width:"1200px"}}>
  <img className="card-img-top0"/>


  <div className="card-body">
  <h5 className="card-title">All Inventory Details</h5>
  <h6 className="card-subtitle mb-2 text-muted">* This is the entire inventory details of the company..!</h6>
  <table className="table table-hover table-bordered border-primary">
  <thead>
    <tr>
      {/*<th scope="col">Item ID</th>*/}
      <th scope="col">Quantity In Stock</th>
        <th scope="col">Item Name</th>
        <th scope="col">Item Category</th>
        <th scope="col">Quantity Of The Used Present</th>
        <th scope="col">Unit Price</th>
        <th scope="col">Total Cost</th>
        <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
    {inventories.map((user,index)=>(
      <tr>{/*<td>{user._id}</td>*/}
      <td>{user.qinstock}</td>
          <td>{user.name}</td>
          <td>{user.category}</td>
          <td>{user.qusedpresent}</td>
          <td>{user.unitprice}</td>
          <td>{user.totcost}</td>
      <td>
 

      <th scope="col">
      <Link className="edit" to={`/update/${user._id}`}>
        <button type="submit" className="btn btn-primary">Update</button>
        </Link></th>
        <th></th>
        <th scope="col">
      <Link className="delete" onClick={()=>deleteInventory(user._id)}>
        
        <button type="submit" name="delete_btn" className="btn btn-danger">Delete</button>
        </Link>
        </th>
     </td>
    </tr>
    ))}
    </tbody>
    </table>
    <table>
            <tr><th>
            <a href="/adding" button type="submit" className="btn btn-success" >Add Inventory</a></th>
            <th></th>
            <th>
            <a href="/home" type="button" class="btn btn-secondary">Cancel Process</a></th>
            </tr></table>
    </div>
    </div></div></div><br/><br/>
    <div className="chart">
    <h2 className="chartTitle">Daily Stock Analytics</h2>
    <ResponsiveContainer width="100%" aspect={4/2}>
      <LineChart data={data1}>
        <XAxis dataKey="name" stroke="#5550bd"/>
        <YAxis dataKey="Used Present" stroke="#5550bd"/>
        <Line type="monotone" dataKey="Quantity In Stock"/>

        
      </LineChart>
    </ResponsiveContainer>

    </div>


</div>





            
   
    );

}

export default AllInventories;
