import React,{useState} from "react";
import './AddSupplier.css';
import "../App.css";
import axios from "axios";
import { useHistory, useParams } from "react-router";
function UpdateSupplier(){
  
 
 

   
    
 let history = useHistory();
 const {id} = useParams();
 const [users,setUsers]= useState({

  
  

        applyby:"",
        compname:"", 
        supaddr:"",
        supdis:"",
        postalcode:"",
        country:"",
        telephone:"",
        fax:"",
        email:""

  });
  const
    {applyby,compname,supaddr,supdis,postalcode,country,telephone,fax,email} = users;
  const onInputChange = e =>{
    setUsers({...users, [e.target.name]:e.target.value});
  };

  useEffect(() =>{
    loadUser();
 }, []);

 const onSubmit = async e =>{
  e.preventDefault();

  await axios.put(`http://localhost:8050/supplier/updates/${id}`,users)
    history.push('/allsup');
    alert("Supplier is updated successfully!")


    };


    const loadUser = async () =>{

      const result = await axios.get(`http://localhost:8050/supplier/geted/${id}`);
      setUsers(result.data);
      
    };

 
  

    return (
      <div className="container">
      <div className="col d-flex justify-content-center">
<div className="card4">
<img className="card4-img-top"/>
      <div className="col-md-8 mt-4 mx-auto">
      <h1 className="h3 mb-3 font-weight-normal">Updating Supplier</h1> 
      <form className="needs-validation"  onSubmit={sendData}>
        <div className="form-group" style={{marginBottom:'15px'}} align="left"><div className="col-xs-4">
          <label style={{marginBottom:'5px'}}>Applying for this by</label></div>
          <input type="radio" id="supplier" name="rb" value={applyby}
                             onChange={(e)=>onInputChange(e)} readOnly required/>a supplier
          <input type="radio" id="supplier" name="rb" value={applyby}
                              onChange={(e)=>onInputChange(e)} readOnly required/>a behalf supplier<br/></div>
                            <div className="form-group" style={{marginBottom:'15px'}} align="left">
                  
<label style={{marginBottom:'5px'}}>Company Name</label><br/>
<input type="text" className="form-control" name="compname" placeholder="Enter company" value={compname} 
onChange={(e)=>onInputChange(e)} readOnly required/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Address</label>
                            <input type="text" 
                            className="form-control"
                            name="supaddr"
                            value={supaddr}
                            placeholder="Enter address" 
                              
                            onChange={(e)=>onInputChange(e)} readOnly required/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Distrinct</label>
                          
