import React from 'react'
import Pdf from "react-to-pdf";
import logo from "../assets/logo.PNG";
import DatePost from './DatePost';
import { Page, Text, View, Document, StyleSheet } from '@react-pdf/renderer';
import "./PDF.css";
import AllInventoryPrint from "./AllInventoryPrint";
const ref=React.createRef();
const PDF =(props)=>{
  const styles = StyleSheet.create({
    page: { backgroundColor: 'tomato' },
    section: { color: 'white', textAlign: 'center', margin: 30 }
  });
  
  const doc = (
 <Document>
      <Page size="A4" style={styles.page}>
        <View style={styles.section}>
          <Text>Section #1</Text>
        </View>
      </Page></Document>
  );

    return (
       
      

        <div className="container">
        <div className="Post" ref={ref}>
        <h3 className="h3 mb-3 font-weight-normal">Inventory Stock Availability Report</h3>        <div className="officialLogo">
 <img className="logoOFF"/></div>
 <h5 className="h5 mb-3 font-weight-normal"><DatePost/></h5> 
        <AllInventoryPrint/>
       
   

  
         
           
           
        
            
     </div>
        <Pdf targetRef={ref} filename="inventory stock availability document.pdf" className="pdfSize">
            {({ toPdf }) => <button onClick={toPdf} class="btn btn-info">Download as PDF</button>}
        </Pdf>
    
        </div>
    );

}
export default PDF;
