import React from 'react'
import Pdf from "react-to-pdf";
import Itemcard from './Itemcard';
import "./PDF.css";
const ref=React.createRef();
const PDF =(props)=>{
    return (
       
      
       <div className="formFormat">
        <div className="container">
        <div className="Post" ref={ref}>
        <h1 className="h3 mb-3 font-weight-normal">Adding New Inventory Item</h1> 
        <div className="officialLogo">
  <img className="card1-img-top" /></div>
        <ul className="list-group list-group-horizontal">
  <li className="list-group-item">Quantity In Stock Currently</li>
  <li className="list-group-item">{props.qinstock}</li>
  
</ul>
<ul class="list-group list-group-horizontal">
  <li class="list-group-item">Item name</li>
  <li class="list-group-item">{props.name}</li>
  
</ul>

        
         <h4>Inventory Item Name :{props.name}</h4>
            <h4>{props.category}</h4>
            <h4>{props.qusedpresent}</h4>
            <h4>{props.unitprice}</h4>
            <h4>{props.totcost}</h4>
        
            
        </div>
        <Pdf targetRef={ref} filename="inventory analysis document.pdf">
            {({ toPdf }) => <button onClick={toPdf} class="btn btn-info">Download as PDF</button>}
        </Pdf>
    
        </div></div>
    );

}
export default PDF;
