import React from 'react'
import Pdf from "react-to-pdf";
import logo from "../assets/logo.PNG";
import DatePost from './DatePost';

import "./PDF.css";
import AllInventoryPrint from "./AllInventoryPrint";
const ref=React.createRef();
const PDF =()=>{


    return (
     
      
       <div className="formFormat">
        <div className="container">
        <div className="Post" ref={ref}>
        <h3 className="h3 mb-3 font-weight-normal" align="left">Inventory Stock Availability Report</h3>  <h5 className="h5 mb-3 font-weight-normal"><DatePost/></h5> <br/>       
        <div className="officialLogo">
 <img className="logoOFF" align="left"/></div><br/><br/>

        <AllInventoryPrint/>
       
    

  

  
         
           
           
        
            
     </div>
        <Pdf targetRef={ref} filename="inventory stock availability document.pdf" className="pdfSize">
            {({ toPdf }) => <button onClick={toPdf} class="btn btn-info" align="left">Download as PDF</button>}
        </Pdf>
    
        </div></div>    
    );

    }
export default PDF;
