import React, { useState, useEffect} from "react";
import axios from "axios";
 
 
function SearchFeature()
{
  const [search,setSearch] =useState('');
  const [record,setRecord] = useState([]);
 
  
    // On Page load display all records 
    const loadEmployeeDetail = async () =>  
    {
      var response = fetch('http://localhost:8050/inventory/')
         .then(function(response){
            return response.json();
          })
         .then(function(myJson) {
            setRecord(myJson);
          });
    }
    useEffect(() => {
      loadEmployeeDetail();
    }, []);
 
    // Search Records here 
    const searchRecords = () =>
    {
        axios.get(`http://localhost:8050/inventory/searchRecord/${search}`)
        .then(response => {
          setRecord(response.data);
        });
         
    }
 
    const loadRecordAgain = () =>
    {
      var response = fetch('http://localhost:8050/inventory')
      .then(function(response){
         return response.json();
       })
      .then(function(myJson) {
         setRecord(myJson);
       });
         
    }
    useEffect(() => {
      loadRecordAgain();
    }, []);
 
 
  return(
    <section>  
    <div class="container">  
    <h4 className="mb-3 text-center mt-4">Search Records in MERN</h4>
      <div class="row mt-3">
      <div class="col-sm-11">
        <div class="input-group mb-4 mt-3">
          <div class="form-outline">
           <input type="text" id="form1"    onKeyDown={loadRecordAgain} onKeyUp={searchRecords} onChange={(e)=>setSearch(e.target.value)} class="form-control" placeholder="Search Employee Here" style={{backgroundColor:"#ececec"}}/>
        </div>
        {/* <button type="button" onClick={searchRecords}  class="btn btn-success">
            <i class="fa fa-search" aria-hidden="true"></i>
        </button> */}
        </div>  
        <table class="table table-hover  table-striped table-bordered ml-4 ">
            <thead>
            <tr>
            <th scope="col">Quantity In Stock</th>
        <th scope="col">Item Name</th>
        <th scope="col">Item Category</th>
        <th scope="col">Quantity Of The Used Present</th>
        <th scope="col">Unit Price</th>
        <th scope="col">Total Cost</th>
        <th scope="col">Action</th>
    </tr>
         
            </thead>
            <tbody>
     
            {record.map((name)=>
                <tr>
                <td>{name.first_name}</td>
                <td>{name.last_name}</td>
                <td>{name.email}</td>
                <td>{name.phone}</td>
                <td>{name.salary}</td>
                <td><img class="img-fluid" src={"/images/" + name.emp_image} style={{maxWidth:"40px"}}  alt=""/></td>
                </tr>
                )} 
            </tbody>
        </table>
      </div>
      </div>
    </div>
   </section>
  )
}
 
export default SearchFeature;
