import React, { useState, useEffect } from 'react';
import axios from "axios";
import {useHistory, useParams} from 'react-router-dom';
import "./AddSupplier.css";


function UpdateSupplier() {


 let history = useHistory();
 const {id} = useParams();
 const [users,setUsers]= useState({
    applyby:"", 
    compname:"",
    supaddr:"",
    supdis:"",
    postalcode:"",
    country:"",
    telephone:"",
    fax:"",
    email:""
 

 });
   
 const{applyby, compname,supaddr,supdis,postalcode,country,telephone,fax,email} = users;
 const onInputChange = e =>{
   setUsers({...users, [e.target.name]:e.target.value});
 };
   
    
      useEffect(() =>{
    loadUser();
 }, []);    

   const onSubmit = async e =>{
   e.preventDefault();

   await axios.put(`http://localhost:8050/supplier/updates/${id}`,users)
     history.push('/');
     alert("Supplier is updated successfully!")

     };


     const loadUser = async () =>{

       const result = await axios.get(`http://localhost:8050/supplier/geted/${id}`);
       setUsers(result.data);
       
     };

/*
  

  function EditInventory(props) {
  let id = props.match.params.id;

  const [data, setData] = useState("");

  useEffect(() => {
    axios.get(`http://localhost:8050/inventory/${id}`).then((res) => {
      setData(res.data);
      console.log(res.data);
    }).catch((err)=>{
      console.log(err);
    })
  },[])

  const onInputChange = e => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };



  function onSubmit() {

    axios.put(`http://localhost:8050/inventory/update/${id}`, data).then(()=>{
      alert("Updated successfully"); 
    }).catch((err) => {
      console.log(err);
    })
    window.location.replace('/');
  };
*/
return (
    <div className="container">
  <div className="col d-flex justify-content-center">
<div className="card4">
<img className="card4-img-top"/>
    <div className="col-md-8 mt-4 mx-auto">
      <h1 className="h3 mb-3 font-weight-normal">Update Existing Supplier</h1> 
      <form className="needs-validation"  onSubmit={e => onSubmit(e)}>

      <div className="form-group" style={{marginBottom:'15px'}} align="left">
              <label style={{marginBottom:'5px'}}>Supplier ID</label><br/>
              <input type="text" className="form-control"   name="id" value={id} onChange={(e)=>onInputChange(e)} readOnly required/>
              </div>

              <div className="form-group1" style={{marginBottom:'15px'}} align="left">
                <label for="applyby" className="form-label" style={{marginBottom:'5px'}}>Applying for this by</label><br/>
                <input type="radio" className="form-control" id="applyby" name="applyby" value={true} onChange={(e)=>onInputChange(e)} required />a supplier
          <input type="radio" id="applyby" name="supplier" id="applyby" value={false} className="form-control" onChange={(e)=>onInputChange(e)} required />a behalf supplier<br/></div>
                
                <div className="form-group2" style={{marginBottom:'15px'}} align="left">
                  <label for="compname" className="form-label" style={{marginBottom:'5px'}}>Company Name</label><br/>
                  <input type="text" className="form-control"  id="compname" placeholder="Enter company name" name="compname" value={compname} onChange={(e)=>onInputChange(e)} required />
                  </div>
                  <div className="form-group3" style={{marginBottom:'15px'}} align="left">
                    <label for="supaddr" className="form-label" style={{marginBottom:'5px'}}>Address</label><br/>
                    <input type="text" className="form-control" id="supaddr"  placeholder="Enter supplier address"  name="supaddr" value={supaddr} onChange={(e)=>onInputChange(e)} required />
                    </div>
                    <div className="form-group4" style={{marginBottom:'15px'}} align="left">
                      <label for="supdis" className="form-label" style={{marginBottom:'5px'}}>Distrinct</label><br/>
                      <input type="text"  className="form-control" id="supdis" name="supdis"  value={supdis} onChange={(e)=>onInputChange(e)} required/>

                      
                           
                            </div>
                            
                            <div className="form-group5" style={{marginBottom:'15px'}} align="left">
                              <label for="postalcode" className="form-label" style={{marginBottom:'5px'}}>Postal Code</label><br/>
                              <input type="text" className="form-control" id="postalcode" name="postalcode" placeholder="Enter postal code" value={postalcode} onChange={(e)=>onInputChange(e)} required />
                              </div>
                              <div className="form-group6" style={{marginBottom:'15px'}} align="left">
                                <label for="country" className="form-label" style={{marginBottom:'5px'}}>Country</label><br/>
                                <input type="text" className="form-control" id="country" name="country" placeholder="Enter country" value={country} onChange={(e)=>onInputChange(e)} required />
                                </div>
                                <div className="form-group7" style={{marginBottom:'15px'}} align="left">
                                <label for="telephone" className="form-label" style={{marginBottom:'5px'}}>Company Telephone</label><br/>
                                <input type="text" className="form-control" id="telephone" name="telephone" placeholder="Enter telephone" value={telephone} onChange={(e)=>onInputChange(e)} required />
                                </div>
                                <div className="form-group8" style={{marginBottom:'15px'}} align="left">
                                <label for="fax" className="form-label" style={{marginBottom:'5px'}}>Fax</label><br/>
                                <input type="text" className="form-control" id="fax" name="fax" placeholder="Enter fax" value={fax} onChange={(e)=>onInputChange(e)} required />
                                </div>
                                <div className="form-group9" style={{marginBottom:'15px'}} align="left">
                                <label for="email" className="form-label" style={{marginBottom:'5px'}}>Email Address</label><br/>
                                <input type="text" className="form-control" id="email" name="email" placeholder="Enter email address" value={email} onChange={(e)=>onInputChange(e)} required />
                                </div>
                                <div className="form-group10" style={{marginBottom:'15px'}} >
                                <table>
                                  <tr>
                                  <th>
                                  <button type="submit" className="btn btn-success">Update and Save</button></th>
                                  <th></th>
                                  <th>
                                    <a href="/home" type="button" class="btn btn-secondary">Cancel Process</a></th>
                                    </tr></table>
                                    </div>
                                    </form>
                                    </div>
                                    </div>
