import React, { useState,useEffect } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";
import "./AllMaterial.css";

const AllMaterials=()=>{
    const [materials, setMaterials] = useState([]);

    useEffect(()=>{
        loadUsers();
    },[]);

    const loadUsers = async ()=>{
        const result = await axios.get("http://localhost:8050/material/");
        setMaterials(result.data.reverse());
    };

    function deleteMaterial(id){
        axios.delete(`http://localhost:8050/material/delete/${id}`).then(()=>{
        /*alert("Delete Successful");*/

        }).catch((err)=>{
            console.log(err);
        })
        window.location.reload();
        alert("Raw material was deleted successfully!");
    }
  

    

    return (
        
            
    <div className="container">

<div className="row">
  <div className="col-lg-9 mt-2 mb-2">
    
</div>


        </div>






<div className="conatiner">

<div className="col d-flex justify-content-center">
  <div className="card5" style={{width:"1200px"}}>
  <img className="card-img-top5"/>


  <div className="card-body">
  <h5 className="card-title">All Raw Material Details</h5>
  <h6 className="card-subtitle mb-2 text-muted">* This is the entire raw material details of the company..!</h6>
  <table className="table table-hover table-bordered border-primary">
  <thead>
    <tr>
      {/*<th scope="col">Item ID</th>*/}
      <th scope="col">Raw Material Name</th>
        <th scope="col">Raw Material Category</th>
        <th scope="col">Material Unit</th>
        <th scope="col">Maximum Stock Level</th>
        <th scope="col">Amount Issued Per Day</th>
        <th scope="col">Unit Price</th>
        <th scope="col">Total Cost</th>
        <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
    {materials.map((user,index)=>(
      <tr>{/*<td>{user._id}</td>*/}
      <td>{user.matname}</td>
          <td>{user.matcategory}</td>
          <td>{user.matunit}</td>
          <td>{user.maxlevel}</td>
          <td>{user.amtissued}</td>
          <td>{user.matunitprice}</td>
          <td>{user.mtotcost}</td>
      <td>
 

      <th scope="col">
      <Link className="edit" to={`/update/${user._id}`}>
        <button type="submit" className="btn btn-primary">Update</button>
        </Link></th>
        <th></th>
        <th scope="col">
      <Link className="delete" onClick={()=>deleteMaterial(user._id)}>
        
        <button type="submit" name="delete_btn" className="btn btn-danger">Delete</button>
        </Link>
        </th>
     </td>
    </tr>
    ))}
    </tbody>
    </table>
    <table>
            <tr><th>
            <a href="/added" button type="submit" className="btn btn-success" >Add Inventory</a></th>
            <th></th>
            <th>
            <a href="/home" type="button" class="btn btn-secondary">Cancel Process</a></th>
            </tr></table>
    </div>
    </div>

</div>
 </div>
</div>





            
   
    )

}

export default AllMaterials;
