import React, { useState, useEffect } from 'react';
import axios from "axios";
import {useHistory, useParams} from 'react-router-dom';



function UpdateInventory() {


 let history = useHistory();
 const {id} = useParams();
 const [users,setUsers]= useState({
    qinstock: "",
    name:"",
    category:"",
    qusedpresent:"",
    unitprice:"",
    totcost:""
 

 });
   
 const{qinstock, name,category,qusedpresent, unitprice,totcost} = users;
 const onInputChange = e =>{
   setUsers({...users, [e.target.name]:e.target.value});
 };
   
    
      useEffect(() =>{
    loadUser();
 }, []);    

   const onSubmit = async e =>{
   e.preventDefault();

   await axios.put(`http://localhost:8050/inventory/update/${id}`,users)
     history.push('/');

     };


     const loadUser = async () =>{

       const result = await axios.get(`http://localhost:8050/inventory/get/${id}`);
       setUsers(result.data);
     };

/*
  

  function EditInventory(props) {
  let id = props.match.params.id;

  const [data, setData] = useState("");

  useEffect(() => {
    axios.get(`http://localhost:8050/inventory/${id}`).then((res) => {
      setData(res.data);
      console.log(res.data);
    }).catch((err)=>{
      console.log(err);
    })
  },[])

  const onInputChange = e => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };



  function onSubmit() {

    axios.put(`http://localhost:8050/inventory/update/${id}`, data).then(()=>{
      alert("Updated successfully"); 
    }).catch((err) => {
      console.log(err);
    })
    window.location.replace('/');
  };
*/
return (
    <div className="col-md-8 mt-4 mx-auto">
      <h1 className="h3 mb-3 font-weight-normal">Update Inventory Item</h1> 
      <form className="needs-validation"  onSubmit={e => onSubmit(e)}>

      <div className="form-group" style={{marginBottom:'15px'}} align="left">
              <label style={{marginBottom:'5px'}}>Item ID</label><br/>
              <input type="text" className="form-control"   name="id" value={id} onChange={(e)=>onInputChange(e)} readOnly required/>
              </div>

              <div className="form-group1" style={{marginBottom:'15px'}} align="left">
                <label for="qinstock" className="form-label" style={{marginBottom:'5px'}}>Qauntity Of The Use Present</label><br/>
                <input type="text" className="form-control" id="qinstock" placeholder="Enter Amount" name="qinstock" value={qinstock}  onChange={(e)=>onInputChange(e)} required  />
                </div>
                <div className="form-group2" style={{marginBottom:'15px'}} align="left">
                  <label for="name" className="form-label" style={{marginBottom:'5px'}}>Item Name</label><br/>
                  <input type="text" className="form-control"  id="name" placeholder="Enter inventory item name" name="name" value={name} onChange={(e)=>onInputChange(e)} required />
                  </div>
                  <div className="form-group3" style={{marginBottom:'15px'}} align="left">
                    <label for="category" className="form-label" style={{marginBottom:'5px'}}>Item Category</label><br/>
                    <input type="text" className="form-control" id="category"  placeholder="Enter category"  name="category" value={category} onChange={(e)=>onInputChange(e)} required />
                    </div>
                    <div className="form-group4" style={{marginBottom:'15px'}} align="left">
                      <label for="qusedpresent" className="form-label" style={{marginBottom:'5px'}}>Quantity Of the use Present</label><br/>
                            <input type="text" className="form-control" id="qusedpresent" name="qusedpresent" value={qusedpresent} placeholder="Enter present use item quantity" onChange={(e)=>onInputChange(e)} required />
                            </div>
                            <div className="form-group5" style={{marginBottom:'15px'}} align="left">
                              <label for="unitprice" className="form-label" style={{marginBottom:'5px'}}>Unit Price</label><br/>
                              <input type="text" className="form-control" id="unitprice" name="unitprice" placeholder="Enter unit price of item" value={unitprice} onChange={(e)=>onInputChange(e)} required />
                              </div>
                              <div className="form-group6" style={{marginBottom:'15px'}} align="left">
                                <label for="totcost" className="form-label" style={{marginBottom:'5px'}}>Total Cost</label><br/>
                                <input type="text" className="form-control" id="totcost" name="totcost" placeholder="Enter total cost of inventory in the stock" value={totcost} onChange={(e)=>onInputChange(e)} required />
                                </div>
                                <div className="form-group7" style={{marginBottom:'15px'}} >
                                <table>
            <tr><th>
            <button type="submit" className="btn btn-success">Update and Save</button></th>
            <th></th>
            <th>
            <button type="button" class="btn btn-secondary" href="/home">Cancel Process</button></th>
            </tr></table></div>

  

      
              
     
      
      </form>
  </div>

);
}
export default UpdateInventory;
