import React,{useState} from "react";
import "../App.css";
export default function AddSupplier(){

  const [applyby, setApplyby] =useState("");
  const [compname, setCompname] =useState("");
  const [supaddr, setSupaddr] =useState("");
  const [supdis, setSupdis] =useState("");
  const [postalcode, setPostalcode] =useState("");
  const [country, setCountry] =useState("");
  const [telephone, setTelephone] =useState("");
  const [fax, setFax] =useState("");
  const [email, setEmail] =useState("");
  function sendData(){
    alert("Supplier is added!");
  }
 
  

    return (
      <div className="col-md-8 mt-4 mx-auto">
      <h1 className="h3 mb-3 font-weight-normal">Adding New Supplier</h1> 
      <form className="needs-validation" noValidate onSubmit={sendData}>
        <div className="form-group" style={{marginBottom:'15px'}} align="left">
          <label style={{marginBottom:'5px'}}>Applying for this by</label><br/>
          <input type="radio" id="supplier" name="supplier"/>a supplier
          <input type="radio" id="supplier" name="supplier"/>a behalf supplier<br/><br/></div>
                            <div className="form-group" style={{marginBottom:'15px'}} align="left">
                  
<label style={{marginBottom:'5px'}}>Company Name</label><br/>
<input type="text" className="form-control" name="compname" placeholder="Enter company" onChange={(e)=>{
setCompname(e.target.value);

}}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Address</label>
                            <input type="text" 
                            className="form-control"
                            name="supaddr"
                            placeholder="Enter address" onChange={(e)=>{
                              setSupaddr(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Distrinct</label>
                          
                            <select class="form-select form-select-sm" aria-label=".form-select-sm example"><option>Ampara</option><option>Anuradhapura</option><option>Badhulla</option><option>Batticaloa</option><option>Colombo</option><option>Galle</option><option>Gampaha</option><option>Hambanthota</option><option>Jaffna</option><option>Kalutara</option><option>Kandy</option><option>Kegalle</option>
                            <option>Kilinochchi</option><option>Manner</option><option>Matale</option><option>Matara</option><option>Monaragala</option><option>Mulativu</option><option>Nuwara Eliya</option>
                            <option>Polonnaruwa</option><option>Puttalam</option><option>Ratnapura</option><option>Trincomalee</option><option>Vavuniya</option></select>

          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Postal Code</label>
                            <input type="text" 
                            className="form-control"
                            name="postalcode"
                            placeholder="Enter postal code" onChange={(e)=>{
                              setPostalcode(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Country</label>
                            <input type="text" 
                            className="form-control"
                            name="country"
                            placeholder="Enter country" onChange={(e)=>{
                              setCountry(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Company Telephone Number</label>
                            <input type="text" 
                            className="form-control"
                            name="telephone"
                            placeholder="Enter telephone number" onChange={(e)=>{
                              setTelephone(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Fax</label>
                            <input type="text" 
                            className="form-control"
                            name="fax"
                            placeholder="Enter fax number" onChange={(e)=>{
                              setFax(e.target.value);
                              
                              }}/>
          </div>
          
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Email Address</label>
                            <input type="email" 
                            className="form-control"
                            name="email"
                            placeholder="ABC@gmail.com" onChange={(e)=>{
                              setEmail(e.target.value);
                              
                              }}/>
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
          <input className="form-check-input" type="checkbox" id="flexCheckDefault" />
          <label className="accept"><b> * I agree that all the information disclosed are truthful and I do understand that might be prosecuted for any false details</b></label>
        
          </div>
         
          <div className="form-group" style={{marginBottom:'15px'}} >

          <button className="btn btn-success" type="submit">
              <i className="far fa-check-square"></i>
              &nbsp; Save
          </button>
          
          <button className="btn btn-primary ml-3" type="submit">
           
           <i className="far fa-window-close"></i>
               &nbsp; Cancel
           </button></div>

  

      
              
     
      
      </form>
  </div>
    );
}
