import React, { useState, useEffect } from 'react';
import axios from "axios";
import {useHistory, useParams} from 'react-router-dom';



function UpdateMaterial() {


 let history = useHistory();
 const {id} = useParams();
 const [users,setUsers]= useState({

    matname:"", 
    matcategory:"",
    matunit:"",
    maxlevel:"",
    amtissued:"",
    matunitprice:"",
    mtotcost: ""
 

 });
   
 const{matname, matcategory,matunit,maxlevel, amtissued,matunitprice,mtotcost} = users;
 const onInputChange = e =>{
   setUsers({...users, [e.target.name]:e.target.value});
 };
   
    
      useEffect(() =>{
    loadUser();
 }, []);    

   const onSubmit = async e =>{
   e.preventDefault();

   await axios.put(`http://localhost:8050/material/update/${id}`,users)
     history.push('/all');


     };


     const loadUser = async () =>{

       const result = await axios.get(`http://localhost:8050/material/get/${id}`);
       setUsers(result.data);
       
     };

