return (
<div>
<div className="mat">
  
      <span>Update Raw Material Item</span></div>
      <div className="add_mat">
      <form onSubmit={e => onSubmit(e)}> <center><h1>Update Raw Materials</h1></center>

    
<div className="gl-0"><label for="id" className="form-label">Material ID</label>
<input type="text" className="form-control"  placeholder="Update material ID" name="id" value ={id} onChange={(e)=>onInputChange(e)} readOnly required />
</div>
<div className="gl-1">
                  <label for="matname" className="form-label">Material Name</label>
                  <input type="text" className="form-control"  id="matname" placeholder="Enter material name" name="matname" value ={matname} onChange={(e)=>onInputChange(e)} required />
              </div>
<div className="gl-2">
                  <label for="matcategory" className="form-label">Material Category</label>
                  <input type="text" className="form-control"  id="matcategory" placeholder="Enter material category" name="matcategory" value ={matcategory} onChange={(e)=>onInputChange(e)} required />
              </div>
<div className="gl-3">
                  <label for="matunit" className="form-label">Material Unit</label>
                  <input type="text" className="form-control"  id="matunit" placeholder="Enter material unit" name="matunit" value ={matunit} onChange={(e)=>onInputChange(e)} required />
              </div>
<div className="gl-4">
                  <label for="maxlevel" className="form-label">Material Level</label>
                  <input type="text" className="form-control"  id="maxlevel" placeholder="Enter material level" name="maxlevel" value ={maxlevel} onChange={(e)=>onInputChange(e)} required />
              </div>
<div className="gl-5">
                  <label for="amtissued" className="form-label">Amount issued</label>
                  <input type="text" className="form-control"  id="amtissued" placeholder="Enter amount issued" name="amtissued" value ={amtissued} onChange={(e)=>onInputChange(e)} required />
              </div>
<div className="gl-6">
                  <label for="matunitprice" className="form-label">Material Unit Price</label>
                  <input type="text" className="form-control"  id="matunitprice" placeholder="Enter material unit price" name="matunitprice" value ={matunitprice} onChange={(e)=>onInputChange(e)} required />
              </div>
<div className="gl-7">
                  <label for="mtotcost" className="form-label">Material Price</label>
                  <input type="text" className="form-control"  id="mtotcost" placeholder="Enter material price" name="mtotcost" value ={mtotcost} onChange={(e)=>onInputChange(e)} required />
              </div>
              <button type="submit" className="btn0 btn-primary">Update Material</button><br></br>
        <a href="/"><button type="submit" className="btn1 btn-primary">Cancel</button></a>



</form>
</div>
</div>


    
      

      
                                   
                                    );
