import React from 'react'
import Pdf from "react-to-pdf";

const ref=React.createRef();
const PDF =(props)=>{
    return (
       <div className="formFormat">
        <div className="container">
        <div className="Post" ref={ref}>
            <table><th scope="col">Quantity In Stock Currently</th><th>{props.qinstock}</th></table>
         <h4>Inventory Item Name :{props.name}</h4>
            <h4>{props.category}</h4>
            <h4>{props.qusedpresent}</h4>
            <h4>{props.unitprice}</h4>
            <h4>{props.totcost}</h4>
        
            
        </div>
        <Pdf targetRef={ref} filename="inventory analysis document.pdf">
            {({ toPdf }) => <button onClick={toPdf} class="btn btn-info">Download as PDF</button>}
        </Pdf>
        </div></div>
    );

}
export default PDF;
