import React, { useState,useEffect } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";
import cake0 from '../assets/cake0.jpg';
const AllInventories=()=>{
    const [inventories, setInventories] = useState([]);

    useEffect(()=>{
        loadUsers();
    },[]);

    const loadUsers = async ()=>{
        const result = await axios.get("http://localhost:8050/inventory/");
        setInventories(result.data.reverse());
    };

    function deleteInventory(id){
        axios.delete(`http://localhost:8050/inventory/delete/${id}`).then(()=>{
        alert("Delete Successful");

        }).catch((err)=>{
            console.log(err);
        })
        window.location.reload();
    }
  
    

    

    return (
        
            
    <div className="container">

<div className="row">
  <div className="col-lg-9 mt-2 mb-2" align="left">
    <h4>All Inventory Details</h4>
</div>

        </div>

<div class="epay">




<div className="conatiner">
  <div class="card" style={{width:"1200px"}}>
  <img src="../assets/cake0.jpg" class="card-img-top" alt="cake0"/>
  <div class="card-body">
  <table className="table table-hover table-bordered border-primary">
  <thead>
    <tr>
      {/*<th scope="col">Item ID</th>*/}
      <th scope="col">Quantity In Stock</th>
        <th scope="col">Item Name</th>
        <th scope="col">Item Category</th>
        <th scope="col">Quantity Of The Used Present</th>
        <th scope="col">Unit Price</th>
        <th scope="col">Total Cost</th>
        <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
    {inventories.map((user,index)=>(
      <tr>{/*<td>{user._id}</td>*/}
      <td>{user.qinstock}</td>
          <td>{user.name}</td>
          <td>{user.category}</td>
          <td>{user.qusedpresent}</td>
          <td>{user.unitprice}</td>
          <td>{user.totcost}</td>
      <td>
 

      <th scope="col">
      <Link className="edit" to={`/update/${user._id}`}>
        <button type="submit" className="btn-primary">Update</button>
        </Link></th>
        <th></th>
        <th scope="col">
      <Link className="delete" onClick={()=>deleteInventory(user._id)}>
        
        <button type="submit" name="delete_btn" className="btn-primary">Delete</button>
        </Link>
        </th>
     </td>
    </tr>
    ))}
    </tbody>
    </table>
    </div>
    </div>

</div>
 </div>
</div>





            
   
    )

}

export default AllInventories;
