return (
    <div className="col-md-8 mt-4 mx-auto">
      <h1 className="h3 mb-3 font-weight-normal">Update Inventory Item</h1> 
      <form className="needs-validation"  onSubmit={e => onSubmit(e)}>
      <div className="form-group" style={{marginBottom:'15px'}} align="left">
              <label style={{marginBottom:'5px'}}>Item ID</label><br/>
              <input type="text" className="form-control"   name="id" value={id} onChange={(e)=>onInputChange(e)} readOnly required/>
              </div>

              <div className="kl-4">

<label for="qinstock" className="form-label">Qauntity Of The Use Present</label>

<input type="text" className="form-control" id="qinstock" placeholder="Enter Amount" name="qinstock" value={qinstock}  onChange={(e)=>onInputChange(e)} required  />

</div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
              <label style={{marginBottom:'5px'}}>Item Name</label>
              <input type="text" className="form-control"  placeholder="Enter inventory item name" value={name} onChange={(e)=>onInputChange(e)} required/>
              </div>
         <div className="form-group" style={{marginBottom:'15px'}} align="left">
             <label style={{marginBottom:'5px'}}>Item Category</label>
             <input type="text" className="form-control"  placeholder="Enter category"  value={category} onChange={(e)=>onInputChange(e)} required/>
            </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
            <label style={{marginBottom:'5px'}}>Quantity Of the use Present</label>
                            <input type="text" 
                            className="form-control"
                             value={qusedpresent}
                            placeholder="Enter present use item quantity" onChange={(e)=>onInputChange(e)} required/>
                            
          </div>
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Unit Price</label>
                            <input type="text" 
                            className="form-control"
                            
                            placeholder="Enter unit price of item" value={unitprice} onChange={(e)=>onInputChange(e)}required/>
                            </div>
        
          
          <div className="form-group" style={{marginBottom:'15px'}} align="left">
                            <label style={{marginBottom:'5px'}}>Total Cost</label>
                            <input type="text" 
                            className="form-control"
                        
                            placeholder="Enter total cost of inventory in the stock" value={totcost} onChange={(e)=>onInputChange(e)} required/>
                             </div>
          
         
          <div className="form-group" style={{marginBottom:'15px'}} >

          <button className="btn btn-success" type="submit">
              <i className="far fa-check-square"></i>
              &nbsp; Save
          </button>
          
          <button className="btn btn-primary ml-3" type="submit">
           
           <i className="far fa-window-close"></i>
               &nbsp; Cancel
           </button></div>

  

      
              
     
      
      </form>
  </div>

);
}
