 let history = useHistory();
 const {id} = useParams();
 const [materials,setMaterials]= useState({
 
    matname: "", 
    matcategory: "",
    matunit: "",
    maxlevel: "",
    amtissued: "",
    matunitprice: "",
    mtotcost: ""
 

 });
   
 const{matname, matcategory,matunit,maxlevel, amtissued,matunitprice,mtotcost} = materials;
 const onInputChange = e =>{
   setMaterials({...materials, [e.target.name]:e.target.value});
 };
   
    
      useEffect(() =>{
    loadMaterial();
 }, []);    

   const onSubmit = async e =>{
   e.preventDefault();

   await axios.put(`http://localhost:8050/material/updated/${id}`,materials)
     history.push('/all');


     };


     const loadMaterial = async () =>{

       const result = await axios.get(`http://localhost:8050/material/gets/${id}`);
       setMaterials(result.data);
       
     };

