              </div>
              <div className="gl-2">
                  <label for="matcategory" className="form-label">Material Category</label>
                  <input type="text" className="form-control"  placeholder="Update Ward ID" name="matcategory" value ={matcategory} onChange={(e)=>onInputChange(e)} required />
              </div>
              <div className="gl-3">
                  <label for="matunit" className="form-label">Material Unit</label>
