              </div>
              <div className="gl-4">
                  <label for="maxlevel" className="form-label">Material Level</label>
                  <input type="text" className="form-control"  placeholder="Update Ward ID" name="maxlevel" value ={maxlevel} onChange={(e)=>onInputChange(e)} required />
              </div>
              <div className="gl-5">
                  <label for="amtissued" className="form-label">Amount issued</label>
                  <input type="text" className="form-control"  placeholder="Update Ward ID" name="amtissued" value ={amtissued} onChange={(e)=>onInputChange(e)} required />
              </div>
              <div className="gl-6">
                  <label for="matunitprice" className="form-label">Material Unit Price</label>
