import React, {useState,useEffect} from 'react';
import axios from "axios";
import { Link } from "react-router-dom";

export default function AllInventories(){
    const [inventories, setInventories] = useState([]);
    useEffect(()=>{
        loadUsers();
    },[]);

    const loadUsers = async ()=>{
        const result = await axios.get("http://localhost:8050/inventory/");
        setInventories(result.data.reverse());
    };

    function deleteInventory(id){
        axios.delete('http://localhost:8050/inventory/delete/${id}').then(()=>{
            alert("Inventory is deleted!");

        }).catch((err)=>{
            console.log(err);
        })
        window.location.reload();
    }

    

    return (
        <div className="container">
            
    <div>

<div className="fun">

<span>Payment Management</span>

</div>

<div class="epay">


<div class="search-container">
  <form className="search">
    <input className="search123" type="text" placeholder="Search Payment" name="search1" />
    <Link to={'/get/:id'} >
      <button className="btn13" type="submit">Search</button>
    </Link>
  </form>
</div>


<div className="all_pay">
  <center><h1>ALL PAYMENTS</h1></center>

  <table className="table table-dark table-striped">
    <thead>
      <tr>
        {/*<th scope="col">Item ID</th>*/} 
        <th scope="col">Quantity In Stock</th>
        <th scope="col">Item Name</th>
        <th scope="col">Item Category</th>
        <th scope="col">Quantity Of The Used Present</th>
        <th scope="col">Unit Price</th>
        <th scope="col">Total Cost</th>
        
      </tr>
    </thead>


    <tbody>

      {inventories.map((user, index) => (
        <tr>

         {/* <td>{user._id}</td>*/}
          <td>{user.qinstock}</td>
          <td>{user.name}</td>
          <td>{user.category}</td>
          <td>{user.qusedpresent}</td>
          <td>{user.unitprice}</td>
          <td>{user.totcost}</td>
     
          <td>
            <th scope="col">
              <Link className="edit" to={`/update/${user._id}`}>
                <button type="submit" className="btn11 btn-primary">Update</button>
              </Link></th>
            <th></th>
            <th scope="col">
              <Link className="delete" onClick={() => deleteInventory(user._id)}>
                <button type="submit" name="delete_btn" className="btn12 btn-primary">Delete</button>
              </Link>

            </th>
          </td>
        </tr>
      ))}
    </tbody>
  </table>
</div>
</div>
</div>
</div>



            
   
    )
}
