import React from 'react'
import Pdf from "react-to-pdf";
import logo from "../assets/logo.PNG";
import DatePost from './DatePost';
import { Page, Text, View, Document} from '@react-pdf/renderer';
import "./PDF.css";
import AllInventoryPrint from "./AllInventoryPrint";
const ref=React.createRef();
const PDF =()=>{


    return (
      <Document> <Page size="A1" style={{ backgroundColor: 'tomato' }}>    <View style={{ color: 'white', textAlign: 'center', margin: 30 }}>
      
       <div className="formFormat">
        <div className="container">
        <div className="Post" ref={ref}>
        <h3 className="h3 mb-3 font-weight-normal">Inventory Stock Availability Report</h3>        <div className="officialLogo">
 <img className="logoOFF"/></div>
 <h5 className="h5 mb-3 font-weight-normal"><DatePost/></h5> 
        <AllInventoryPrint/>
       
    
  

        <Text>Section #1</Text>

                   
     </div>
        <Pdf targetRef={ref} filename="inventory stock availability document.pdf" className="pdfSize"><Page size="A1" style={{backgroundColor:"red"}}/>
            {({ toPdf }) => <button onClick={toPdf} class="btn btn-info">Download as PDF</button>}
        </Pdf>
    
        </div></div>       </View>
 </Page></Document>
    );

    }
export default PDF;
