import React, { Component } from 'react';
import PDF from './PDF';
import './Post.css';
import './AllInventory.css'

class Post extends Component {
    state = {
        inid: '',
        qinstock: '',
        name: '',
        category: '',
        qusedpresent:'',
        unitprice:'',
        totcost:'',
        postSubmitted: false
    }

 

    sunmitPost = (e) => {
        
        if(!this.state.inid||!this.state.qinstock || !this.state.name || !this.state.category || !this.state.qusedpresent||!this.state.unitprice||!this.state.totcost){
            alert('All fields are required!');
            e.preventDefault();
        }else{
            this.setState({
                postSubmitted: true
            });
        }
    }

    render(){
        return(
            <>
                {  !this.state.postSubmitted ? 
                    (<div className="container">
                       
                            
  {/*Table view for display all the data*/}
  <table className="table table-hover table-bordered border-primary">
  <thead>
    <tr>
      {/*<th scope="col">Item ID</th>*/}
      <th scope="col">Quantity In Stock</th>
        <th scope="col">Item Name</th>
        <th scope="col">Item Category</th>
        <th scope="col">Quantity Of The Used Present</th>
        <th scope="col">Unit Price</th>
        <th scope="col">Total Cost</th>
        <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
    {inventories.map((user,index)=>(
      <tr className="raw2">{/*<td>{user._id}</td>*/}
      <td>{user.qinstock}</td>
          <td>{user.name}</td>
          <td>{user.category}</td>
          <td>{user.qusedpresent}</td>
          <td>{user.unitprice}</td>
          <td>{user.totcost}</td>
      <td>
 

      <th scope="col">
      <Link className="edit" to={`/update/${user._id}`}>
        <button type="submit" className="btn btn-primary">Update</button>
        </Link></th>
        <th></th>
        <th scope="col">
      <Link className="delete" onClick={()=>deleteInventory(user._id)}>
        
        <button type="submit" name="delete_btn" className="btn btn-danger">Delete</button>
        </Link>
        </th>
     </td>
    </tr>
    ))}
    </tbody>
    </table>
                    </div>) : (
                        <PDF inid={this.state.inid} qinstock={this.state.qinstock} name={this.state.name} category={this.state.category} qusedpresent={this.state.qusedpresent} unitprice={this.state.unitprice} totcost={this.state.totcost} />
                        
                    )
                }
            </>
        );
    }
}

export default Post;
